def save_descriptions():
    conn = sqlite3.connect(DB_PATH)
    cursor = conn.cursor()

    saved = 0
    for event_id, description in DESCRIPTIONS.items():
        cursor.execute("""
            UPDATE events SET
                full_description_gr = ?,
                updated_at = datetime('now')
            WHERE id = ?
        """, (description, event_id))

        if cursor.rowcount > 0:
            print(f"âœ… Saved: {event_id}")
            saved += 1
        else:
            print(f"âš ï¸  Not found: {event_id}")

    conn.commit()
    conn.close()

    print(f"\nğŸ“Š Total saved: {saved}")

